import React, { useState, useEffect  }  from 'react'

import { Spinner, Row, column} from 'react-bootstrap'
import { fetchMemories } from '../axios/index.js'



const homeScreen = () => {

    const [memories, setMemories] = useState([])

    useEffect(() =>{
    const getMemories = async ()=>{
        const {data} = await fetchMemories()
        console.log(data)
        setMemories(data)
    }
    getMemories()


    }, [])
     
    return (
        
        <  >
            <h2 >  WELCOME TO THE HOMSESCREEN </h2>
             /*  html icinde srarement kullanmak icin  curly bracket acariz */
            
            {
                !memories.length ? <Spinner animation ='border' />   : 

                <Row>

                </Row>
            
            }
            
        </>

        
    )
}

export default homeScreen
